import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import './ResearchBar';
import ResearchBar from './ResearchBar';
import Widget from './Widget';


class App extends Component {

  state={
    jsonres: null,
    val: null
  }

  getDataFromChild = (data) =>{
    this.setState({val:data});
  }

  fetchData(){


    alert(this.state.val);
    const api_key = '343a692fdcf51315a134a9317dd7fc9d';
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${this.state.val}&APPID=${api_key}`;

    fetch(url)
      .then(response => response.json())
      .then(data => this.setState({ jsonres:data }))
      .catch(e => console.log('error', e));
  }




  render() {
    return (
      <div className="App">
        <ResearchBar callbackFromParent={this.getDataFromChild}/>
        {this.state.val && this.fetchData}
        <Widget/>



      </div>
    );
  }
}

export default App;
