import React, { Component } from 'react';
import $ from 'jquery';
import 'reactstrap';

class ResearchBar extends Component {

  state = { jsonres: null }
  
  handleSubmit = (event) => {
    let val = document.getElementById("cityName").value;

    event.preventDefault();
    const api_key = '343a692fdcf51315a134a9317dd7fc9d';
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${val}&APPID=${api_key}`;
    
    fetch(url)
      .then(response => response.json())
      .then(data => this.setState({ jsonres:data }))
      .then(()=>{this.updateWidget()})
      .catch(e => console.log('error', e));

  }

  updateWidget = () =>{
      console.log(this.state.jsonres);
      if(this.state.jsonres["cod"]==404){
        $("#cityName").addClass("has-error");
        $("#cityName").removeClass("has-success");

        $(".feedback").show();
        $(".feedback").text("City not found");
      }
      else{
        $("#cityName").addClass("has-success");
        $("#cityName").removeClass("has-error");
        $(".feedback").hide();

