        }
      }
  }
  
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>

          <div className="form-group">
            <h2>City name</h2>
            <input type="text" className="form-control form-control-lg" id="cityName" />
            <div className="feedback"></div>
          </div>
          <div id="weatherwidget"></div>
        
        </form>
      </div>
    );
    
  }
  
}

export default ResearchBar;
