import React, { Component } from 'react';

class ResearchBar extends Component {

  state = { jsonres: null }
  
  handleSubmit = (event) => {
    let val = document.getElementById("cityName").value;

    event.preventDefault();
    const api_key = '343a692fdcf51315a134a9317dd7fc9d';
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${val}&APPID=${api_key}`;
    
    fetch(url)
      .then(response => response.json())
      .then(data => this.setState({ jsonres:data }))
      .then(()=>{this.updateWidget()})
      .catch(e => console.log('error', e));

  }

  updateWidget = () =>{
      console.log(this.state.jsonres);
      if(this.state.jsonres["cod"]==404)
        document.getElementById("stile").classList.add("has-error");
  }
  
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div class="form-group has-danger" id="stile">
            <h2>City name</h2>
            <input type="text" className="form-control form-control-lg " id="cityName" />
          </div>
        </form>
      </div>
    );
    
  }
  
}

export default ResearchBar;
