import React, { Component } from 'react';

class ResearchBar extends Component {

  state = { jsonres: null }
  
  handleSubmit = (event) => {
    alert("swag");
    let val = document.getElementById("cityName").value;

    event.preventDefault();
    const api_key = '343a692fdcf51315a134a9317dd7fc9d';
    const url = `http://api.openweathermap.org/data/2.5/weather?q=${val}&APPID=${api_key}`;
    
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.setState({ jsonres:data });
      }).then(()=>{this.getJson();})
      .catch(e => console.log('error', e));

  }

  getJson = () =>{
    if(this.state.jsonres!=null);
      console.log(this.state.jsonres);
  }
  
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div class="form-group">
            <h2>City name</h2>
            <input type="text" class="form-control form-control-lg" id="cityName" />
          </div>
        </form>
      </div>
    );
    
  }
  
}

export default ResearchBar;
